jQuery.getDateDiff=function(i){var m="";if(isNaN(i)){return m}var e=1000*60;var f=e*60;var j=f*24;var g=j*30;var b=new Date().getTime();var h=b-i;if(h<0){return""}var d=h/g;var a=h/(7*j);var l=h/j;var k=h/f;var c=h/e;if(d>12){m="很久很久以前"}else{if(d>=1){m=parseInt(d)+"个月前"}else{if(a>=1){m=parseInt(a)+"周前"}else{if(l>=1){m=parseInt(l)+"天前"}else{if(k>=1){m=parseInt(k)+"个小时前"}else{if(c>=1){m=parseInt(c)+"分钟前"}else{m="刚刚"}}}}}}return m};